#pragma once

enum HttpRequestMethod : uint16_t {
  HTTP_GET     = 0b0000000001,
  HTTP_POST    = 0b0000000010,
  HTTP_DELETE  = 0b0000000100,
  HTTP_PUT     = 0b0000001000,
  HTTP_PATCH   = 0b0000010000,
  HTTP_HEAD    = 0b0000100000,
  HTTP_OPTIONS = 0b0001000000,
  HTTP_CONNECT = 0b0010000000,
  HTTP_TRACE   = 0b0100000000,
  HTTP_ANY     = 0b0111111111,
};